package Pisyst;

public class Demo10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=0; i<2; i++)
		{
			System.out.println(i);
			System.out.println("java is great");
			if(i==2)
			{
				System.out.println("Ending the loop");
				break;
			}
		}
		System.out.println("Loop ending here");

	}

}
